import React , {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import './button.js';

//adding const (non changing aka consistent) keys here 
const api  = {
key : "6b681c1d69a01510027518ec6e3aac83",
base : "https://api.openweathermap.org/data/2.5"
}

const dateBuild = (D) => {
  let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
  let days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"]; 

let day = days[D.getDay()];
let date = D.getDate();
let month = months[D.getMonth()];
let year = D.getTheYear();
}
 

function App() {
  
  return (
    <div className = "weather-app">
      <header className = "weather-app">
      <h1>React Weather App</h1>
      </header>
     <main>
       <div className= "search-bar">
         <input 
         type= "text"
         className="search-bar"
         placeholder="Enter a city..."
         />
       </div> 
           <div className=" location-box">
           <div className = "description-box"></div>
           <div className = "date">{dateBuild(newDate())}</div>
       </div>
     </main>
     <footer>
       <h6>Created by Jamell, Bev , Kenneth, Meltem and Hazel</h6>
     </footer>
    </div>
  );
}

export default App;
