import { getSuggestedQuery, render } from '@testing-library/react';
import React, { useState } from 'react';
import { createRenderer, renderIntoDocument } from 'react-dom/test-utils';
import  api_key from '../apiKeys';
import api_key_forecast from '../apiKeys';

function SearchBar() {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState({});
    const [nextweather, setNextWeather] = useState({})




    const searchCurrent = (evt) => {
      if (evt.key === "Submit") {
        fetch(`${api_key.base}${city}&appid=${api_key.key}`)
        .then(res => res.json())
        .then(result =>{
          setWeather(result);
          setCity('');
          console.log(result);
        });
      }
    }

    const searchNextWeek = (evt) => {
      if (evt.key === "Submit") {
        fetch(`${api_key_forecast.base}${city}&appid=${api_key_forecast.key}`)
        .then(res => res.json())
        .then(result =>{
          setNextWeather(result);
          setNextCity('');

        });
      }
    }


    function SearchLastWeek() {

        // need to do for buttton 

    }


    const dateBuilder = (d) => {
        let months = [
          "January",
          "February",
          "March",
          "April",
          "May",
          "June",
          "July",
          "August",
          "September",
          "Novemebr",
          "December",
        ];

        let days =[
          "Sunday",
          "Monday",
          "Tuesday",
          "Wednesday",
          "Thursday",
          "Friday",
          "Saturday"
        ];

        let day = days[d.getDay()];
        let date = d.getDate();
        let month = months[d.getMonth()];
        let year = d.getFullYear();

        return `${day} ${date} ${month} ${year}`;
    };

