import { getSuggestedQuery, render } from '@testing-library/react';
import React, { useState } from 'react';
import { createRenderer, renderIntoDocument } from 'react-dom/test-utils';
import api_key from '../apiKeys';
import Clock from "react-live-clock";
import Forecast from './Forecast';
import ReactAnimatedWeather from "react-animated-weather";
import loader from "../Images/WeatherIcons.gif"

function SearchBar() {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState({});




    const search = evt => {
      if (evt.key === "Enter") {
        fetch(`${api_key.base}${city}&appid=${api_key.key}`)
        .then(res => res.json())
        .then(result =>{
          setWeather(result);
          setCity('');
          console.log(result);
        });
      }
    }

    const dateBuilder = (d) => {
        let months = [
          "January",
          "February",
          "March",
          "April",
          "May",
          "June",
          "July",
          "August",
          "September",
          "Novemebr",
          "December",
        ];

        let days =[
          "Sunday",
          "Monday",
          "Tuesday",
          "Wednesday",
          "Thursday",
          "Friday",
          "Saturday"
        ];

        let day = days[d.getDay()];
        let date = d.getDate();
        let month = months[d.getMonth()];
        let year = d.getFullYear();

        return `${day} ${date} ${month} ${year}`;
    };

    const defaults = {
      color: "white",
      size: 112,
      animate: true,

    };

class Weather extends React.Component {
  stats = {
    lat: undefined, 
    lon: undefined,
    errorMessage: undefined,
    temperatureC: undefined, 
    temperatureF: undefined, 
    city: undefined,
    country: undefined, 
    humidity: undefined, 
    description: undefined, 
    icon: "CLEAR_DAY",
    sunrise: "undefined",
    sunset: "undefined",
    errorMsg: "undefined",
  }





render() {
  if (this.state.temperatureC) {
    return (
      <React.Fragment>
        <div className="city">
          <div className="title">
            <h2>{this.state.city}</h2>
            <h3>{this.state.country}</h3>
          </div>
          <div className="mb-icon">
            {" "}
            <ReactAnimatedWeather
              icon={this.state.icon}
              color={defaults.color}
              size={defaults.size}
              animate={defaults.animate}
            />
            <p>{this.state.main}</p>
          </div>
          <div className="date-time">
            <div className="dmy">
              <div id="txt"></div>
              <div className="current-time">
                <Clock format="HH:mm:ss" interval={1000} ticking={true} />
              </div>
              <div className="current-date">{dateBuilder(new Date())}</div>
            </div>
            <div className="temperature">
              <p>
                {this.state.temperatureC}Â°<span>C</span>
              </p>
              {/* <span className="slash">/</span>
              {this.state.temperatureF} &deg;F */}
            </div>
          </div>
        </div>
        <Forcast icon={this.state.icon} weather={this.state.main} />
      </React.Fragment>
    );
  } else {
    return (
      <React.Fragment>
        <img src={loader} style={{ width: "50%", WebkitUserDrag: "none" }} />
        <h3 style={{ color: "white", fontSize: "22px", fontWeight: "600" }}>
          Detecting your location
        </h3>
        <h3 style={{ color: "white", marginTop: "10px" }}>
          Your current location wil be displayed on the App <br></br> & used
          for calculating Real time weather.
        </h3>
      </React.Fragment>
      );
    }
  }
}
}

export default SearchBar;
